    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Calendrier
{% endblock %}
{% block stylesheets %}
\t<style>
\t\t#calendrier {
\t\t\twidth: 80%;
\t\t\tmargin: auto;
\t\t}
\t</style>
{% endblock %}
{% block body %}
\t<div id=\"calendrier\"></div>
{% endblock %}

{% block javascripts %}
\t<script>
\t\twindow.onload = () => {
            let calendarElt = document.querySelector(\"#calendrier\")

            let calendar = new FullCalendar.Calendar(calendarElt, {
                initialView: 'timeGridWeek',
                locale:'fr',
                themeSystem: 'bootstrap',
                timeZone: 'Europe/Paris',
                headerToolbar: {
                    start: 'prev,next today',
                    center: 'title',
                    end: 'dayGridMonth,timeGridWeek'
                },
                events: {{ data|raw}},
                editable: true,
                selectable:true,
                eventResizableFromStart: true
            })

            calendar.on('eventChange', (e)=>{
                let url = `/api/\${e.event.id}/edit`
                let donnees = {
                    \"title\":e.event.title,
                    \"description\":e.event.extendedProps.description,
                    \"start\":e.event.start,
                    \"end\":e.event.end,
                    \"backgroundColor\":e.event.backgroundColor,
                    \"borderColor\":e.event.borderColor,
                    \"textColor\":e.event.textColor,
                    \"allDay\":e.event.allDay
                }
                let xhr = new XMLHttpRequest
                xhr.open(\"PUT\", url)
                xhr.send(JSON.stringify(donnees))
            })

            function Select(start, end) {
                var title = prompt(\"Event Content:\");
                var eventData;
                if (title) {
                    eventData = {
                    title: title,
                    start: start,
                    end: end
                    };
                    \$(\"#calendrier\").fullCalendar(\"renderEvent\", eventData, true); // stick? = true
                }
                \$(\"#calendrier\").fullCalendar(\"unselect\");
            }

            function eventRender(event, element) {
                element
                    .find(\".fc-content\")
                    .prepend(\"<span class='closeon material-icons'>&#xe5cd;</span>\");
                element.find(\".closeon\").on(\"click\", function() {
                    \$(\"#calendar\").fullCalendar(\"removeEvents\", event._id);
                });
            }

            function eventClick(calEvent) {
                var title = prompt(\"Edit Event Content:\", calEvent.title);
                calEvent.title = title;
                \$(\"#calendar\").fullCalendar(\"updateEvent\", calEvent);
            }

            calendar.render()
}

\t</script>
{% endblock %}
", "main/index.html.twig", "C:\\Projet_dev\\fullCalendar\\templates\\main\\index.html.twig");
